import {Component} from "react"


export class Counter extends Component {
   
   addFeedback = () => {
       this.setState(prevState =>{
           return { 
               good: 5,
            }   
       })
   }
    state = {
        good: 0,
        neutral: 0,
        bad: 0
      }
   render() {
       return (

        <div className="load-more">
            <p className="load-more">Please leave a feedback</p>

            <button className="load-more" onClick={this.addFeedback}>Good</button>
            <button className="load-more">Neutral</button>
            <button className="load-more">Bad</button>
            <h2>Statistics</h2>
            <p >Good</p>
            <span >{this.state.good}</span>
            <p className="load-more">Neutral</p>
            <span className="load-more">{this.state.neutral}</span>
            <p className="load-more">Bad</p>
            <span className="load-more">{this.state.bad}</span>


        </div>);
   }
}
