import {Component} from "react"


export class Counter extends Component {
    state = {
        good: 0,
        neutral: 0,
        bad: 0,
        //visible:false,
      }
   addFeedbackGood = (e) => {
   const feedbackType = e.target.name;
       this.setState(prevState =>{
           return { 
            [feedbackType]: prevState[feedbackType] + 1,
            //visible: true,
            };   
       });
      
   }


   countTotalFeedback= () => {
    
       return this.state.neutral +this.state.bad +this.state.good;
   }

   countPositiveFeedbackPercentage= () => {
    const total = this.state.neutral +this.state.bad +this.state.good;
       return this.state.good/total *100;
   }

   render() {
       return (

        <div className="load-more">
            <p className="load-more">Please leave a feedback</p>

            <button name="good" className="load-more" onClick={this.addFeedbackGood} >Good</button>
            <button name="neutral" className="load-more" onClick={this.addFeedbackGood}>Neutral</button>
            <button name="bad" className="load-more" onClick={this.addFeedbackGood}>Bad</button>
            <h2>Statistics</h2>
            <p >Good</p>
            <p>{this.state.good}</p>
            <p className="load-more">Neutral</p>
            <p className="load-more">{this.state.neutral}</p>
            <p className="load-more">Bad</p>
            <p className="load-more">{this.state.bad}</p>
    
           
            {this.state.bad || this.state.good ||this.state.neutral ( <div><p className="load-more">Total:{this.countTotalFeedback()} </p>
            <p className="load-more">Positive feedback:{this.countPositiveFeedbackPercentage()}%</p>
            </div>
    
           )}

        </div>);
   }
}
